 &lt;!DOCTYPE html&gt; &lt;html lang=&#34;zh-CN&#34;&gt; &lt;head&gt; &lt;meta charset=&#34;UTF-8&#34;&gt; &lt;meta name=&#34;viewport&#34; content=&#34;width=device-width, initial-scale=1.0&#34;&gt; &lt;title&gt;本地图片OCR工具 - 极简版&lt;/title&gt; &lt;script src=&#34;https://cdn.tailwindcss.com&#34;&gt;&lt;/script&gt; &lt;link href=&#34;https://cdn.jsdelivr.net/npm/font-awesome@4.7.0/css/font-awesome.min.css&#34; rel=&#34;stylesheet&#34;&gt; &lt;script src=&#34;https://cdn.jsdelivr.net/npm/tesseract.js@5.1.0/dist/tesseract.min.js&#34;&gt;&lt;/script&gt; &lt;style&gt; .drag-active { border-color: #10b981; background-color: #ecfdf5; } /* 隐藏滚动条但允许滚动 */ textarea::-webkit-scrollbar { width: 8px; } textarea::-webkit-scrollbar-thumb { background-color: #cbd5e1; border-radius: 4px; } &lt;/style&gt; &lt;/head&gt; &lt;body class=&#34;bg-slate-50 min-h-screen flex flex-col font-sans text-slate-800&#34;&gt; &lt;header class=&#34;bg-white shadow-sm py-4 px-6&#34;&gt; &lt;div class=&#34;container mx-auto flex justify-between items-center&#34;&gt; &lt;h1 class=&#34;text-xl font-bold text-slate-800&#34;&gt;&lt;i class=&#34;fa fa-cube text-blue-500 mr-2&#34;&gt;&lt;/i&gt;本地OCR识别&lt;/h1&gt; &lt;span class=&#34;text-xs text-slate-500 bg-slate-100 px-2 py-1 rounded&#34;&gt;本地运算 · 不上传服务器&lt;/span&gt; &lt;/div&gt; &lt;/header&gt; &lt;main class=&#34;flex-grow container mx-auto py-6 px-4 grid grid-cols-1 md:grid-cols-2 gap-6&#34;&gt; &lt;div class=&#34;bg-white rounded-xl shadow-md p-6 flex flex-col h-full&#34;&gt; &lt;h2 class=&#34;font-semibold mb-4 text-slate-700&#34;&gt;&lt;i class=&#34;fa fa-upload text-blue-500 mr-2&#34;&gt;&lt;/i&gt;上传图片&lt;/h2&gt; &lt;div id=&#34;dropArea&#34; class=&#34;flex-1 border-2 border-dashed border-slate-300 rounded-lg flex flex-col items-center justify-center p-6 transition-colors cursor-pointer min-h-[200px] relative&#34;&gt; &lt;input type=&#34;file&#34; id=&#34;fileInput&#34; accept=&#34;image/*&#34; class=&#34;hidden&#34;&gt; &lt;div id=&#34;uploadPrompt&#34; class=&#34;text-center&#34;&gt; &lt;i class=&#34;fa fa-cloud-upload text-4xl text-slate-300 mb-3&#34;&gt;&lt;/i&gt; &lt;p class=&#34;text-sm text-slate-500&#34;&gt;点击或拖拽图片至此&lt;/p&gt; &lt;/div&gt; &lt;img id=&#34;previewImg&#34; class=&#34;hidden max-h-64 max-w-full object-contain rounded shadow-sm z-10&#34;&gt; &lt;button id=&#34;clearBtn&#34; class=&#34;hidden absolute top-2 right-2 bg-red-500 text-white w-6 h-6 rounded-full hover:bg-red-600 z-20 items-center justify-center text-xs&#34;&gt; &lt;i class=&#34;fa fa-times&#34;&gt;&lt;/i&gt; &lt;/button&gt; &lt;/div&gt; &lt;div id=&#34;progressBox&#34; class=&#34;hidden mt-4&#34;&gt; &lt;div class=&#34;flex justify-between text-xs text-slate-500 mb-1&#34;&gt; &lt;span&gt;正在识别...&lt;/span&gt; &lt;span id=&#34;progressText&#34;&gt;0%&lt;/span&gt; &lt;/div&gt; &lt;div class=&#34;h-2 bg-slate-100 rounded-full overflow-hidden&#34;&gt; &lt;div id=&#34;progressBar&#34; class=&#34;h-full bg-blue-500 transition-all duration-300 w-0&#34;&gt;&lt;/div&gt; &lt;/div&gt; &lt;/div&gt; &lt;button id=&#34;runBtn&#34; disabled class=&#34;mt-4 w-full py-3 rounded-lg font-medium text-white bg-slate-300 cursor-not-allowed transition-colors flex justify-center items-center&#34;&gt; 开始识别 &lt;/button&gt; &lt;/div&gt; &lt;div class=&#34;bg-white rounded-xl shadow-md p-6 flex flex-col h-full&#34;&gt; &lt;div class=&#34;flex justify-between items-center mb-4&#34;&gt; &lt;h2 class=&#34;font-semibold text-slate-700&#34;&gt;&lt;i class=&#34;fa fa-file-text text-emerald-500 mr-2&#34;&gt;&lt;/i&gt;识别结果&lt;/h2&gt; &lt;button id=&#34;copyBtn&#34; class=&#34;text-sm text-blue-500 hover:text-blue-700 disabled:text-slate-300&#34; disabled&gt; &lt;i class=&#34;fa fa-copy mr-1&#34;&gt;&lt;/i&gt;复制 &lt;/button&gt; &lt;/div&gt; &lt;div class=&#34;flex-grow relative bg-slate-50 rounded-lg border border-slate-200&#34;&gt; &lt;textarea id=&#34;resultBox&#34; class=&#34;w-full h-full p-4 bg-transparent resize-none focus:outline-none text-sm leading-relaxed&#34; placeholder=&#34;等待识别...&#34; readonly&gt;&lt;/textarea&gt; &lt;div id=&#34;toast&#34; class=&#34;absolute bottom-4 right-4 bg-slate-800 text-white text-xs px-3 py-1.5 rounded opacity-0 transition-opacity pointer-events-none&#34;&gt; 已复制 &lt;/div&gt; &lt;/div&gt; &lt;/div&gt; &lt;/main&gt; &lt;script&gt; // 元素引用 const els = { dropArea: document.getElementById(&#39;dropArea&#39;), fileInput: document.getElementById(&#39;fileInput&#39;), previewImg: document.getElementById(&#39;previewImg&#39;), uploadPrompt: document.getElementById(&#39;uploadPrompt&#39;), clearBtn: document.getElementById(&#39;clearBtn&#39;), runBtn: document.getElementById(&#39;runBtn&#39;), progressBox: document.getElementById(&#39;progressBox&#39;), progressBar: document.getElementById(&#39;progressBar&#39;), progressText: document.getElementById(&#39;progressText&#39;), resultBox: document.getElementById(&#39;resultBox&#39;), copyBtn: document.getElementById(&#39;copyBtn&#39;), toast: document.getElementById(&#39;toast&#39;) }; let currentFile = null; // --- 工具函数：清理文本空格 --- // 1. 去除中文字符之间的空格 // 2. 将多个空行合并为一个换行 // 3. 去除每行首尾空格 function cleanText(text) { return text .replace(/([^\x00-\xff])\s+([^\x00-\xff])/g, &#39;$1$2&#39;) // 去除汉字间空格 .replace(/[
]+/g, &#39;
&#39;) // 合并多个换行符 .split(&#39;
&#39;).map(line =&gt; line.trim()).filter(line =&gt; line.length &gt; 0).join(&#39;
&#39;) // 去空行并Trim每一行 .trim(); } // --- 交互逻辑 --- // 拖拽效果 [&#39;dragenter&#39;, &#39;dragover&#39;].forEach(e =&gt; els.dropArea.addEventListener(e, (ev) =&gt; { ev.preventDefault(); els.dropArea.classList.add(&#39;drag-active&#39;); })); [&#39;dragleave&#39;, &#39;drop&#39;].forEach(e =&gt; els.dropArea.addEventListener(e, (ev) =&gt; { ev.preventDefault(); els.dropArea.classList.remove(&#39;drag-active&#39;); })); // 文件处理 const handleFile = (file) =&gt; { if (!file || !file.type.startsWith(&#39;image/&#39;)) return alert(&#39;请上传图片文件&#39;); currentFile = file; const reader = new FileReader(); reader.onload = (e) =&gt; { els.previewImg.src = e.target.result; els.previewImg.classList.remove(&#39;hidden&#39;); els.clearBtn.classList.remove(&#39;hidden&#39;); els.uploadPrompt.classList.add(&#39;hidden&#39;); // 激活按钮 els.runBtn.disabled = false; els.runBtn.className = &#39;mt-4 w-full py-3 rounded-lg font-medium text-white bg-blue-500 hover:bg-blue-600 transition-colors shadow-md hover:shadow-lg&#39;; // 重置结果区 els.resultBox.value = &#39;&#39;; els.copyBtn.disabled = true; els.progressBox.classList.add(&#39;hidden&#39;); }; reader.readAsDataURL(file); }; els.dropArea.addEventListener(&#39;drop&#39;, (e) =&gt; handleFile(e.dataTransfer.files[0])); els.dropArea.addEventListener(&#39;click&#39;, (e) =&gt; { if(e.target !== els.clearBtn &amp;&amp; e.target.parentNode !== els.clearBtn) els.fileInput.click(); }); els.fileInput.addEventListener(&#39;change&#39;, (e) =&gt; handleFile(e.target.files[0])); // 清除图片 els.clearBtn.addEventListener(&#39;click&#39;, (e) =&gt; { e.stopPropagation(); currentFile = null; els.previewImg.classList.add(&#39;hidden&#39;); els.clearBtn.classList.add(&#39;hidden&#39;); els.uploadPrompt.classList.remove(&#39;hidden&#39;); els.runBtn.disabled = true; els.runBtn.className = &#39;mt-4 w-full py-3 rounded-lg font-medium text-white bg-slate-300 cursor-not-allowed transition-colors&#39;; els.fileInput.value = &#39;&#39;; }); // 执行OCR els.runBtn.addEventListener(&#39;click&#39;, async () =&gt; { if (!currentFile) return; els.progressBox.classList.remove(&#39;hidden&#39;); els.runBtn.disabled = true; els.runBtn.classList.add(&#39;opacity-75&#39;); try { const { data: { text } } = await Tesseract.recognize(currentFile, &#39;chi_sim&#39;, { logger: m =&gt; { if (m.status === &#39;recognizing text&#39;) { const pct = Math.floor(m.progress * 100) + &#39;%&#39;; els.progressBar.style.width = pct; els.progressText.innerText = pct; } } }); // 关键：使用 cleanText 处理结果 const finalText = cleanText(text); els.resultBox.value = finalText || &#39;未能识别出文字&#39;; els.copyBtn.disabled = !finalText; } catch (err) { els.resultBox.value = &#39;识别出错: &#39; + err.message; } finally { els.runBtn.disabled = false; els.runBtn.classList.remove(&#39;opacity-75&#39;); } }); // 复制功能 els.copyBtn.addEventListener(&#39;click&#39;, () =&gt; { els.resultBox.select(); document.execCommand(&#39;copy&#39;); els.toast.classList.remove(&#39;opacity-0&#39;); setTimeout(() =&gt; els.toast.classList.add(&#39;opacity-0&#39;), 2000); }); &lt;/script&gt; &lt;/body&gt; &lt;/html&gt;
